awk -F, 'NR > 1 { system("oc get pv $(oc get pvc --namespace=\"" $1 "\" --no-headers=true -o jsonpath='\''{.items[*].spec.volumeName}'\'') -o json | jq '\''del(.items[].metadata.creationTimestamp)'\'' | jq '\''del(.items[].metadata.uid)'\'' | jq '\''del(.items[].spec.claimRef.uid)'\''| jq '\''del(.items[].metadata.resourceVersion)'\'' | jq '\''del(.items[].status)'\'' |  jq '\''del(.items[].spec.claimRef.resourceVersion)'\'' | tee pv-" $1 ".json") }' namespace.csv